import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as l,e as i}from"./app.f8ee6222.js";const r={},p=i('<h2 id="js\u57FA\u7840\u8BED\u6CD5\u4E0E\u8868\u8FBE\u5F0F" tabindex="-1"><a class="header-anchor" href="#js\u57FA\u7840\u8BED\u6CD5\u4E0E\u8868\u8FBE\u5F0F" aria-hidden="true">#</a> JS\u57FA\u7840\u8BED\u6CD5\u4E0E\u8868\u8FBE\u5F0F</h2><p>ECMAScript\u662FJavaScript\u7684\u6807\u51C6<br> 1997\u5E74,\u6B27\u6D32\u8BA1\u7B97\u673A\u5236\u9020\u5546\u534F\u4F1A(ECMA)\u8BBE\u7F6E\u4E86JavaScript\u7684\u6807\u51C6,\u547D\u540D\u4E3AECMAScript</p><p>JavaScript\u7684\u4E66\u5199\u4F4D\u7F6E</p><p>\u5728&lt;body&gt;\u4E2D&lt;script&gt;\u6807\u7B7E\uFF0C\u5728\u5185\u90E8\u4E66\u5199JavaScript\u4EE3\u7801</p><p>\u5C06\u4EE3\u7801\u5355\u72EC\u4FDD\u5B58\u4E3A.js\u683C\u5F0F\u6587\u4EF6,\u7136\u540E\u5728HTML\u6587\u4EF6\u4E2D\u4F7F\u7528&lt;script src=&quot;&quot;&gt;&lt;/script&gt;\u8FD9\u6837\u7684\u5F62\u5F0F\u5F15\u5165\u5B83</p><p>JavaScript\u4E0D\u80FD\u8131\u79BBHTML\u7F51\u9875\u8FD0\u884C\uFF08NodeJS\u5C06\u6210\u4E3AJavaScript\u72EC\u7ACB\u7684\u8FD0\u884C\u5E73\u53F0\uFF09</p><h3 id="\u8F93\u51FA\u8BED\u53E5" tabindex="-1"><a class="header-anchor" href="#\u8F93\u51FA\u8BED\u53E5" aria-hidden="true">#</a> \u8F93\u51FA\u8BED\u53E5</h3><p>alert()\u8BED\u53E5\uFF0C\u5F39\u51FA\u8B66\u544A\u6846</p><p>console.log()\u8BED\u53E5\uFF0C\u63A7\u5236\u53F0\u8F93\u51FA</p><h3 id="\u53D8\u91CF" tabindex="-1"><a class="header-anchor" href="#\u53D8\u91CF" aria-hidden="true">#</a> \u53D8\u91CF</h3><p>\u58F0\u660E\u53D8\u91CF</p><p>var a = 5;</p><p>\u53D8\u91CF\u7684\u5408\u6CD5\u547D\u540D\uFF1A<br> \u53EA\u80FD\u7531\u5B57\u6BCD\u3001\u6570\u5B57\u3001\u4E0B\u5212\u7EBF\u3001$\u7EC4\u6210\uFF0C\u4F46\u4E0D\u80FD\u4EE5\u6570\u5B57\u5F00\u5934<br> \u4E0D\u80FD\u5173\u952E\u5B57\u6216\u4FDD\u7559\u5B57<br> \u53D8\u91CF\u540D\u5927\u5C0F\u5199\u654F\u611F\uFF0Ca\u548CA\u4E24\u4E2A\u4E0D\u540C\u7684\u53D8\u91CF</p><p>\u8FD9\u4E9B\u89C4\u5219\u5C31\u662F\u6807\u8BC6\u7B26\u7684\u547D\u540D\u89C4\u5219\uFF0C\u51FD\u6570\u3001\u7C7B\u540D\u3001\u5BF9\u8C61\u7684\u5C5E\u6027\u7B49\u4E5F\u90FD\u8981\u9075\u5B88\u8FD9\u4E2A\u547D\u540D\u89C4\u5219\u3002</p><p>\u4E00\u4E2A\u53D8\u91CF\u53EA\u5B9A\u4E49\uFF0C\u4F46\u6CA1\u6709\u8D4B\u521D\u59CB\u503C\uFF0C\u9ED8\u8BA4\u503C\u662F<code>undefined</code><br> \u4E00\u4E2A\u53D8\u91CF\u53EA\u6709\u88ABvar\u5B9A\u4E49\uFF0C\u5E76\u8D4B\u521D\u59CB\u503C\u4E4B\u540E\uFF0C\u624D\u7B97\u6B63\u5F0F\u521D\u59CB\u5316\u5B8C\u6210</p><p>\u5982\u679C\u4E0D\u7528var\u5B9A\u4E49\uFF0C\u800C\u76F4\u63A5\u5C06\u503C\u8D4B\u4E88\u5B83\uFF0C\u867D\u4E0D\u5F15\u53D1\u62A5\u9519\uFF0C\u4F46\u4F1A\u4EA7\u751F\u4F5C\u7528\u4E8E\u95EE\u9898\u3002</p><p>\u4F7F\u7528\u9017\u53F7\u540C\u65F6\u58F0\u660E\u548C\u521D\u59CB\u5316\u4E24\u4E2A\u53D8\u91CF</p><p>var a = 0, b = 0;</p><p>\u53D8\u91CF\u58F0\u660E\u63D0\u5347</p><p>\u4F60\u53EF\u4EE5\u63D0\u524D\u4F7F\u7528\u4E00\u4E2A\u7A0D\u540E\u624D\u58F0\u660E\u7684\u53D8\u91CF\uFF0C\u800C\u4E0D\u4F1A\u5F15\u53D1\u5F02\u5E38<br> \u5728\u6267\u884C\u6240\u6709\u4EE3\u7801\u524D\uFF0CJS\u6709\u9884\u89E3\u6790\u9636\u6BB5\uFF0C\u4F1A\u9884\u8BFB\u6240\u6709\u53D8\u91CF\u7684\u5B9A\u4E49<br> \u53D8\u91CF\u58F0\u660E\u63D0\u5347\u53EA\u63D0\u5347\u5B9A\u4E49\uFF0C\u4E0D\u63D0\u5347\u503C<br> \u53D8\u91CF\u58F0\u660E\u63D0\u5347\u662F\u63D0\u5347\u81F3\u5F53\u524D\u4F5C\u7528\u57DF\u7684\u6240\u6709\u8BED\u53E5\u4E4B\u524D</p><h3 id="\u6570\u636E\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u7C7B\u578B" aria-hidden="true">#</a> \u6570\u636E\u7C7B\u578B</h3><p>JS\u4E2D\u7684\u4E24\u5927\u7C7B\u6570\u636E\u7C7B\u578B</p><ul><li>\u57FA\u672C\u6570\u636E\u7C7B\u578B <ul><li>Number</li><li>String</li><li>Boolean</li><li>Undefined</li><li>Null</li></ul></li><li>\u590D\u6742\u6570\u636E\u7C7B\u578B <ul><li>Object</li><li>Array</li><li>Function</li><li>RegExp</li><li>Date</li><li>Map</li><li>Set</li><li>\u7B49\u7B49</li></ul></li></ul><p>typeof\u8FD0\u7B97\u7B26<br> \u4F7F\u7528typeof\u8FD0\u7B97\u7B26\u53EF\u4EE5\u68C0\u6D4B\u503C\u6216\u8005\u53D8\u91CF\u7684\u7C7B\u578B</p><h4 id="number" tabindex="-1"><a class="header-anchor" href="#number" aria-hidden="true">#</a> Number</h4><p>\u6240\u6709\u6570\u5B57\u4E0D\u5206\u5927\u5C0F\u3001\u4E0D\u5206\u6574\u6D6E\u3001\u4E0D\u5206\u6B63\u8D1F\uFF0C\u90FD\u662F\u6570\u5B57\u7C7B\u578B<br> \u5C0F\u6570\u4E2D0\u53EF\u4EE5\u7701\u7565</p><p>\u79D1\u5B66\u8BA1\u6570\u6CD5</p><p>var a = 3e8;// 300000000 var b = 2e-4; //0.0002</p><p>\u4E0D\u540C\u8FDB\u5236\u7684\u6570\u5B57<br> \u4E8C\u8FDB\u5236\u6570\u503C\u4EE50b\u5F00\u5934\uFF0C 0b10 =&gt; 2<br> \u516B\u8FDB\u5236\u6570\u503C\u4EE50\u5F00\u5934\uFF0C017 =&gt; 15<br> \u5341\u516D\u8FDB\u5236\u4EE50x\u5F00\u5934\uFF0C0xf =&gt; 15</p><p>\u4E00\u4E2A\u7279\u6B8A\u7684\u6570\u5B57\u578B\u503CNan<br> NaN\uFF08not a number\uFF09\uFF0C\u5373\u201C\u4E0D\u662F\u4E00\u4E2A\u6570\u201D\uFF0C\u4F46\u5B83\u662F\u4E00\u4E2A\u6570\u5B57\u7C7B\u578B\u7684\u503C</p><p>typeof NaN; //number</p><p>0\u9664\u4EE50\u7684\u7ED3\u679C\u662FNaN\uFF0C\u4E8B\u5B9E\u4E0A\uFF0C\u5728\u6570\u5B66\u8FD0\u7B97\u4E2D\uFF0C\u82E5\u7ED3\u679C\u4E0D\u80FD\u5F97\u5230\u6570\u5B57\uFF0C\u5176\u7ED3\u679C\u5F80\u5F80\u90FD\u662FNaN<br> NaN\u6709\u4E00\u4E2A\u5947\u602A\u7684\u6027\u8D28\uFF0C\u4E0D\u81EA\u7B49</p><h4 id="string" tabindex="-1"><a class="header-anchor" href="#string" aria-hidden="true">#</a> String</h4><p>\u52A0\u53F7\u53EF\u4EE5\u7528\u6765\u62FC\u63A5\u591A\u4E2A\u5B57\u7B26\u4E32</p><p>\u2018a\u2019+\u2018b\u2019 // &#39;ab&#39;</p><p>\u5B57\u7B26\u4E32\u7684length\u5C5E\u6027\u8868\u793A\u5B57\u7B26\u4E32\u7684\u957F\u5EA6</p><p>\u5B57\u7B26\u4E32\u5E38\u7528\u7684\u65B9\u6CD5</p><ul><li>charAt() \u83B7\u53D6\u6307\u5B9A\u4F4D\u7F6E\u5B57\u7B26\u4E32</li><li>substring() \u63D0\u53D6\u5B50\u4E32</li><li>substr() \u63D0\u53D6\u5B50\u4E32</li><li>slice() \u63D0\u53D6\u5B50\u4E32</li><li>toUpperCase() \u5C06\u5B57\u7B26\u4E32\u53D8\u4E3A\u5927\u5199</li><li>toLowerCase() \u89C1\u5B57\u7B26\u4E32\u53D8\u4E3A\u5C0F\u5199</li><li>indexOf() \u68C0\u7D22\u5B57\u7B26\u4E32</li></ul><p>charAt(index)\u65B9\u6CD5\u5F97\u5230\u6307\u5B9A\u4F4D\u7F6E\u5B57\u7B26\uFF0C\u4F4D\u7F6E\u4ECE0\u5F00\u59CB substring(a,b)\u65B9\u6CD5\u5F97\u5230\u4ECEa\u5F00\u59CB\u5230b\u7ED3\u675F\uFF08\u4E0D\u5305\u62ECb\u5904\uFF09\u7684\u5B50\u4E32\uFF0C\u5982\u679C\u7701\u7565\u7B2C\u4E8C\u4E2A\u53C2\u6570\uFF0C\u8FD4\u56DE\u7684\u5B50\u4E32\u4F1A\u4E00\u76F4\u5230\u5B57\u7B26\u4E32\u7ED3\u5C3E\u3002a\u53EF\u4EE5\u5927\u4E8Eb\uFF0C\u6570\u5B57\u987A\u5E8F\u5C06\u81EA\u52A8\u8C03\u6574\u4E3A\u5C0F\u6570\u5728\u524D<br> substr(a,b)\u65B9\u6CD5\u5F97\u5230\u4ECEa\u5F00\u59CB\u7684\u957F\u5EA6\u4E3Ab\u7684\u5B50\u4E32\uFF0Cb\u53EF\u4EE5\u7701\u7565\uFF0C\u8868\u793A\u5230\u5B57\u7B26\u4E32\u7ED3\u5C3E\uFF0Ca\u53EF\u4EE5\u662F\u8D1F\u6570\uFF0C\u8868\u793A\u5012\u6570\u4F4D\u7F6E\uFF08\u5012\u6570\u4F4D\u7F6E\u4ECE-1\u5F00\u59CB\uFF09<br> slice(a,b)\u65B9\u6CD5\u5F97\u5230\u4ECEa\u5F00\u59CB\u5230b\u7ED3\u675F\uFF08\u4E0D\u5305\u62ECb\u5904\uFF09\u7684\u5B50\u4E32\uFF0C\u53C2\u6570a\u53EF\u4EE5\u662F\u8D1F\u6570\uFF0C\u53C2\u6570a\u5FC5\u987B\u5C0F\u4E8E\u53C2\u6570b<br> indexOf()\u65B9\u6CD5\u8FD4\u56DE\u67D0\u4E2A\u6307\u5B9A\u7684\u5B57\u7B26\u4E32\u503C\u5728\u5B57\u7B26\u4E32\u4E2D\u9996\u6B21\u51FA\u73B0\u7684\u4F4D\u7F6E\uFF0C\u5982\u679C\u6CA1\u6709\u51FA\u73B0\uFF0C\u5219\u8FD4\u56DE-1</p><h4 id="boolean" tabindex="-1"><a class="header-anchor" href="#boolean" aria-hidden="true">#</a> boolean</h4><p>\u5E03\u5C14\u7C7B\u578B</p><h4 id="undefined" tabindex="-1"><a class="header-anchor" href="#undefined" aria-hidden="true">#</a> Undefined</h4><p>\u4E00\u4E2A\u6CA1\u6709\u88AB\u8D4B\u503C\u7684\u53D8\u91CF\u7684\u9ED8\u8BA4\u503C\u662Fundefined\uFF0C\u800Cundefined\u7684\u7C7B\u578B\u4E5F\u662Fundefined<br> \u5373\uFF1A undefined\u53C8\u662F\u503C\uFF0C\u53C8\u662F\u4E00\u79CD\u7C7B\u578B\uFF0C\u8FD9\u79CD\u7C7B\u578B\u53EA\u6709\u5B83\u81EA\u5DF1\u4E00\u4E2A\u503C</p><h4 id="null" tabindex="-1"><a class="header-anchor" href="#null" aria-hidden="true">#</a> Null</h4><p>null\u8868\u793A\u7A7A\uFF0C\u5B83\u662F\u7A7A\u5BF9\u8C61<br> \u5F53\u6211\u4EEC\u9700\u8981\u5C06\u5BF9\u8C61\u9500\u6BC1\u3001\u6570\u7EC4\u9500\u6BC1\u6216\u8005\u5220\u9664\u4E8B\u4EF6\u76D1\u542C\u65F6\uFF0C\u901A\u5E38\u5C06\u4ED6\u4EEC\u8BBE\u7F6E\u4E3Anull<br> \u7528typeof\u68C0\u6D4Bnull\u503C\uFF0C\u7ED3\u679C\u662Fobject</p><h3 id="\u6570\u636E\u7C7B\u578B\u7684\u8F6C\u6362" tabindex="-1"><a class="header-anchor" href="#\u6570\u636E\u7C7B\u578B\u7684\u8F6C\u6362" aria-hidden="true">#</a> \u6570\u636E\u7C7B\u578B\u7684\u8F6C\u6362</h3><h4 id="\u5176\u4ED6\u503C-\u6570\u5B57" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6\u503C-\u6570\u5B57" aria-hidden="true">#</a> \u5176\u4ED6\u503C -&gt; \u6570\u5B57</h4><p>\u4F7F\u7528Number()\u51FD\u6570\uFF0C\u7EAF\u6570\u5B57\u5B57\u7B26\u4E32\u80FD\u53D8\u4E3A\u6570\u5B57\uFF0C\u4E0D\u662F\u7EAF\u6570\u5B57\u7684\u5B57\u7B26\u4E32\u5C06\u8F6C\u4E3ANaN\u3002\u5E03\u5C14\u503C\u771F\u4E3A1\uFF0C\u5047\u4E3A0\u3002</p><p>Number(&#39;123&#39;); //123 Number(&#39;abc&#39;); // NaN Number(&#39;&#39;); //0 Number(&#39; &#39;); //0</p><p>Number(true); //1 Number(false); //0</p><p>Number(undefined); //NaN Number(null); //0</p><p>parseInt()\u51FD\u6570\u7684\u529F\u80FD\u662F\u5C06\u5B57\u7B26\u4E32\u8F6C\u4E3A\u6574\u6570\uFF0C\u8BE5\u51FD\u6570\u4F1A\u81EA\u52A8\u622A\u6389\u7B2C\u4E00\u4E2A\u975E\u6570\u5B57\u5B57\u7B26\u4E4B\u540E\u7684\u6240\u6709\u5B57\u7B26\u3002\u5176\u4E2D.\u4E5F\u662F\u4E00\u4E2A\u975E\u6570\u5B57\u5B57\u7B26\uFF0C\u5982\u679C\u5B57\u7B26\u4E32\u4E0D\u662F\u4EE5\u6570\u5B57\u5F00\u5934\u7684\uFF0C\u5219\u8F6C\u4E3ANaN</p><p>parseInt(\u20182.11\u2019); //2 parseInt(&#39;3.14\u662F\u5706\u5468\u7387&#39;); //3 parseInt(\u2018\u5706\u5468\u7387\u662F3.14\u2019); //NaN parseInt(&#39;3.99&#39;); // 3</p><p>parseFloat()\u51FD\u6570\u7684\u529F\u80FD\u662F\u5C06\u5B57\u7B26\u4E32\u8F6C\u4E3A\u6D6E\u70B9\u6570</p><p>parseFloat(\u20182.11\u2019); //2.11 parseFloat(&#39;3.14\u662F\u5706\u5468\u7387&#39;); //3.14 parseFloat(\u2018\u5706\u5468\u7387\u662F3.14\u2019); //NaN parseFloat(&#39;3.99&#39;); // 3.99 parseFloat(true); //NaN parseFloat(false); //Nan</p><h4 id="\u5176\u4ED6\u503C-\u5B57\u7B26\u4E32" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6\u503C-\u5B57\u7B26\u4E32" aria-hidden="true">#</a> \u5176\u4ED6\u503C -&gt; \u5B57\u7B26\u4E32</h4><p>\u4F7F\u7528String()\u51FD\u6570\uFF0C\u6570\u5B57\u8F6C\u4E3A\u5B57\u7B26\u4E32\u65F6\uFF0C\u53D8\u4E3A\u2018\u957F\u5F97\u76F8\u540C\u2019\u7684\u5B57\u7B26\u4E32\uFF0C\u79D1\u5B66\u8BA1\u6570\u6CD5\u548C\u975E10\u8FDB\u5236\u6570\u5B57\u4F1A\u8F6C\u4E3A10\u8FDB\u5236\u7684\u503C</p><p>String(123); // &#39;124&#39; String(123.4); // &#39;123.4&#39; String(2e3); // &#39;2000&#39; String(NaN); // &#39;NaN&#39; String(Infinity); // &#39;Infinity&#39; String(0xf); // &#39;15&#39;</p><p>String(true); // &#39;true&#39; String(false); // &#39;false&#39;</p><p>String(indefined); // &#39;undefined&#39; String(null); // &#39;null&#39;</p><p>\u4F7F\u7528toString()\u65B9\u6CD5\uFF0C\u51E0\u4E4E\u6240\u6709\u503C\u90FD\u6709toString()\u65B9\u6CD5</p><h4 id="\u5176\u4ED6\u503C-\u5E03\u5C14\u503C" tabindex="-1"><a class="header-anchor" href="#\u5176\u4ED6\u503C-\u5E03\u5C14\u503C" aria-hidden="true">#</a> \u5176\u4ED6\u503C -&gt; \u5E03\u5C14\u503C</h4><p>\u4F7F\u7528Boolean()\u51FD\u6570\uFF0C\u6570\u503C\u7C7B\u578B\u8F6C\u4E3A\u5E03\u5C14\u503C\u65F6\uFF0C0\u548CNaN\u8F6C\u4E3Afalse\uFF0C\u5176\u4ED6\u6570\u5B57\u90FD\u8F6C\u4E3Atrue\u3002\u7A7A\u5B57\u7B26\u4E32\u8F6C\u4E3Afalse\uFF0C\u5176\u4ED6\u90FD\u8F6C\u4E3Atrue\u3002undefined\u548Cnull\u8F6C\u4E3Afasle</p><p>Boolean(123); // true Boolean(0); // false Boolean(NaN); // false Boolean(Infinity); // true Boolean(-Infinity); // true</p><p>Boolean(&#39;&#39;); // false Boolean(&#39;abc&#39;); // true Boolean(&#39;false&#39;); // true</p><p>Boolean(undefined); // false Boolean(null); // false</p><p>\u5C0F\u6D4B\u8BD5\uFF1A\u4F7F\u7528prompt()\u63A5\u6536\u53C2\u6570\uFF0C\u7F16\u5199\u4E00\u4E2A\u8BA1\u7B97\u5668\u7A0B\u5E8F</p><h3 id="\u590D\u6742\u6570\u636E\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#\u590D\u6742\u6570\u636E\u7C7B\u578B" aria-hidden="true">#</a> \u590D\u6742\u6570\u636E\u7C7B\u578B</h3><p>\u590D\u6742\u6570\u636E\u7C7B\u578B\u90FD\u662F\u201C\u5F15\u7528\u7C7B\u578B\u201D</p><h2 id="\u8868\u8FBE\u5F0F\u548C\u8FD0\u7B97\u7B26" tabindex="-1"><a class="header-anchor" href="#\u8868\u8FBE\u5F0F\u548C\u8FD0\u7B97\u7B26" aria-hidden="true">#</a> \u8868\u8FBE\u5F0F\u548C\u8FD0\u7B97\u7B26</h2><p>\u8868\u8FBE\u5F0F\u79CD\u7C7B</p><ul><li>\u7B97\u672F</li><li>\u5173\u7CFB</li><li>\u903B\u8F91</li><li>\u8D4B\u503C</li><li>\u7EFC\u5408</li></ul><h3 id="\u7B97\u672F\u8FD0\u7B97\u7B26" tabindex="-1"><a class="header-anchor" href="#\u7B97\u672F\u8FD0\u7B97\u7B26" aria-hidden="true">#</a> \u7B97\u672F\u8FD0\u7B97\u7B26</h3><ul><li>\u52A0 +</li><li>\u51CF -</li><li>\u4E58 *</li><li>\u9664 /</li><li>\u53D6\u4F59 %</li></ul><p>\u9690\u5F0F\u7C7B\u578B\u8F6C\u6362<br> \u5982\u679C\u53C2\u4E0E\u6570\u5B66\u8FD0\u7B97\u7684\u67D0\u64CD\u4F5C\u6570\u4E0D\u662F\u6570\u5B57\u578B\uFF0C\u90A3\u4E48JavaScript\u4F1A\u81EA\u52A8\u5C06\u6B64\u64CD\u4F5C\u6570\u8F6C\u4E3A\u6570\u5B57\u578B\u3002\u9664\u4E86\u52A0\u53F7\uFF0C\u6709\u8FDE\u63A5\u64CD\u4F5C\u7B26\u542B\u4E49\u3002 \u9690\u5F0F\u8F6C\u6362\u7684\u672C\u8D28\u662F\u5185\u90E8\u8C03\u7528Number()\u51FD\u6570</p><p>3 * &#39;4&#39;; //12 true + true; //2 false + 2; //2 3 * &#39;2day&#39;; //NaN 3 + undefined; //NaN</p><p>\u6709\u5173IEEE754<br> \u5728JavaScript\u4E2D\uFF0C\u6709\u4E9B\u5C0F\u6570\u7684\u6570\u5B66\u8FD0\u7B97\u4E0D\u662F\u5F88\u7CBE\u51C6</p><p>0.1 + 0.2; //0.30000000000000004</p><p>JavaScript\u4F7F\u7528\u4E86IEEE754\u4E8C\u8FDB\u5236\u6D6E\u70B9\u7B97\u672F\u6807\u51C6\uFF0C\u8FD9\u4F1A\u4F7F\u4E00\u4E9B\u4E2A\u522B\u7684\u5C0F\u6570\u8FD0\u7B97\u4EA7\u751F\u201C\u7CBE\u5EA6\u4E22\u5931\u201D\u95EE\u9898<br> IEEE754\u4E8C\u8FDB\u5236\u6D6E\u70B9\u6570\u7B97\u672F\u6807\u51C6\u662F\u8BA1\u7B97\u673A\u5E95\u5C42\u7F16\u8BD1\u6807\u51C6<br> \u89E3\u51B3\u529E\u6CD5\uFF1A\u5728\u8FDB\u884C\u5C0F\u6570\u8FD0\u7B97\u65F6\uFF0C\u8981\u8C03\u7528\u6570\u5B66\u7684toFixed()\u65B9\u6CD5\u4FDD\u7559\u6307\u5B9A\u7684\u5C0F\u6570\u4F4D\u6570</p><p>\u5E42\u548C\u5F00\u6839\u53F7<br> JS\u4E2D\u6CA1\u6709\u63D0\u4F9B\u5E42\u8BA1\u7B97\u548C\u5F00\u6839\u53F7\u7684\u8FD0\u7B97\u7B26\uFF0C\u9700\u8981\u4F7F\u7528Math\u5BF9\u8C61\u7684\u76F8\u5173\u65B9\u6CD5\u8FDB\u884C\u8BA1\u7B97\u3002</p><p>Math.pow(2,3); //2\u76843\u6B21\u65B9 Math.sqrt(81); //81\u6839\u53F7\u8FD0\u7B979</p><p>\u5411\u4E0A\u53D6\u6574\u548C\u5411\u4E0B\u53D6\u6574</p><p>Math.ceil(2.4); //\u5411\u4E0A\u53D6\u6574 3 Math.floor(2.4); //\u5411\u4E0B\u53D6\u6574 2 Math.ceil(-2.4); //2 Math.floor(-2.5); //-3</p><h3 id="\u5173\u7CFB\u8FD0\u7B97\u7B26" tabindex="-1"><a class="header-anchor" href="#\u5173\u7CFB\u8FD0\u7B97\u7B26" aria-hidden="true">#</a> \u5173\u7CFB\u8FD0\u7B97\u7B26</h3><ul><li>\u5927\u4E8E &gt;</li><li>\u5C0F\u4E8E &lt;</li><li>\u5927\u4E8E\u6216\u7B49\u4E8E &gt;=</li><li>\u5C0F\u4E8E\u6216\u7B49\u4E8E &lt;=</li><li>\u7B49\u4E8E ==</li><li>\u4E0D\u7B49\u4E8E !=</li><li>\u5168\u7B49\u4E8E ===</li><li>\u5168\u4E0D\u7B49\u4E8E !===</li></ul><p>\u76F8\u7B49\u548C\u5168\u7B49</p><ul><li><p>\u4E24\u4E2A\u7B49\u53F7==\u8FD0\u7B97\u7B26\u4E0D\u6BD4\u8F83\u503C\u7684\u7C7B\u578B\uFF0C\u5B83\u4F1A\u8FDB\u884C\u9690\u5F0F\u8F6C\u6362\u540E\u6BD4\u8F83\u503C\u662F\u5426\u76F8\u7B49</p></li><li><p>\u4E09\u4E2A\u7B49\u53F7===\u8FD0\u7B97\u7B26\uFF0C\u4E0D\u4EC5\u6BD4\u8F83\u503C\u662F\u5426\u76F8\u540C\uFF0C\u4E5F\u6BD4\u8F83\u7C7B\u578B\u662F\u5426\u76F8\u540C</p><p>5 == &#39;5&#39;; //true 5 === &#39;5&#39;; //false</p><p>1 == true; //true 1 === true; //false</p><p>0 == false; //true 0 === false; //false</p><p>0 == undefined; //false 0 === undefined; //false</p><p>undefined == null; //true undefined === null; //false</p><p>NaN == NaN; //false NaN === NaN; //false</p></li></ul><p>null\u548Cundefined\u7528==\u6BD4\u8F83\u6D89\u53CA\u9690\u5F0F\u5F3A\u5236\u8F6C\u6362\uFF0CES5\u89C4\u8303\u4E2D\u89C4\u5B9A</p><ul><li>\u5982\u679Cx\u4E3Anull\uFF0Cy\u4E3Aundefined\uFF0C\u5219\u7ED3\u679C\u4E3Atrue</li><li>\u5982\u679Cx\u4E3Aundefined\uFF0Cy\u4E3Anull\uFF0C\u5219\u7ED3\u679C\u4E3Atrue</li></ul><p>null\u548Cundefined\u7528===\u6BD4\u8F83\u7ED3\u679C\u4E3Afalse\uFF0C\u56E0\u4E3A\u7C7B\u578B\u4E0D\u540C</p><ul><li>typeof null -&gt; object</li><li>typeof undefined -&gt; undefined</li></ul><p>NaN\u4E0D\u81EA\u7B49\uFF0CisNaN()\u51FD\u6570\u53EF\u4EE5\u7528\u6765\u5224\u65AD\u53D8\u91CF\u503C\u662F\u5426\u4E3ANaN\uFF0C\u4F46isNaN()\u4E5F\u4E0D\u597D\u7528\uFF0C\u5B83\u7684\u673A\u7406\u662F\uFF1A\u53EA\u8981\u8BE5\u53D8\u91CF\u4F20\u5165Number()\u7684\u6267\u884C\u7ED3\u679C\u662FNaN\uFF0C\u5219isNaN\u51FD\u6570\u90FD\u4F1A\u5F97\u5230true\u3002</p><p>isNaN(undefined); //true isNaN(&#39;3\u5929&#39;); //true isNaN(null); //false</p><p>\u4E0D\u76F8\u7B49\u548C\u4E0D\u5168\u7B49</p><p>!= \u8868\u793A\u4E0D\u76F8\u7B49\uFF0C!==\u8868\u793A\u4E0D\u5168\u7B49</p><p>JS\u4E2D\u6CA1\u6709\u8FDE\u6BD4<br> \u5224\u65ADa\u662F\u5426\u4ECB\u4E8E3\u523015\u4E4B\u95F4</p><h3 id="\u903B\u8F91\u8868\u8FBE\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u903B\u8F91\u8868\u8FBE\u5F0F" aria-hidden="true">#</a> \u903B\u8F91\u8868\u8FBE\u5F0F</h3><ul><li>\u975E !</li><li>\u4E0E &amp;&amp;</li><li>\u6216 ||</li></ul><p>\u975E\u8FD0\u7B97\u7B26</p><p>!true //false !false //true !0 //true !undefined //true !&#39;&#39; //true !&#39;hell&#39; //false</p><p>!!true //true !!false //false !!0 //false !!&#39;&#39; //false !!&#39;hello&#39; //true</p><p>\u53EF\u4EE5\u4F7F\u7528\u4E24\u4E2A!!\u67E5\u770B\u67D0\u4E2A\u7684\u503C\u662F\u6B63\u6027\u8FD8\u662F\u5047\u6027</p><p>\u77ED\u8DEF\u8BA1\u7B97<br> a &amp;&amp; b\u8FD0\u7B97\u4E2D\uFF1Aa\u771F\uFF0C\u8868\u8FBE\u5F0F\u503C\u4E3Ab\uFF1Ba\u5047\uFF0C\u8868\u8FBE\u5F0F\u4E3Aa</p><p>3 &amp;&amp; 6 //6 6 &amp;&amp; 3 //3 undefined &amp;&amp; 3 //undefined 15 &amp;&amp; undefined //undefined null &amp;&amp; 2 //null &#39;&#39; &amp;&amp; 16 //&#39;&#39; NaN &amp;&amp; undefined //NaN</p><p>a || b\u8FD0\u7B97\u4E2D\uFF1Aa\u771F\uFF0C\u8868\u8FBE\u5F0F\u503C\u4E3Aa\uFF1Ba\u5047\u8868\u8FBE\u5F0F\u503C\u4E3Ab</p><p>3 || 6 //3 6 || 3 //6 0 || 3 //3 null || undefined //undefined &#39;a&#39; || &#39;b&#39; //&#39;a&#39; NaN || null //null</p><p>\u903B\u8F91\u8FD0\u7B97\u987A\u5E8F<br> \u903B\u8F91\u8FD0\u7B97\u7684\u4F18\u5148\u7EA7\u662F\uFF1A\u975E -&gt; \u4E0E -&gt; \u6216</p><p>\u8D4B\u503C\u8FD0\u7B97\u7B26</p><ul><li>\u8D4B\u503C =</li><li>\u5FEB\u6377\u8D4B\u503C <ul><li>+=</li><li>-=</li><li>*=</li><li>/=</li><li>%=</li></ul></li><li>\u81EA\u589E ++</li><li>\u81EA\u51CF --</li></ul><p>\u8D4B\u503C\u8FD0\u7B97\u4E5F\u4EA7\u751F\u503C\uFF0C\u7B49\u53F7\u540E\u9762\u7684\u503C\u5C06\u4F5C\u4E3A\u201C\u8D4B\u503C\u8FD0\u7B97\u7684\u503C\u201D</p><p>var a; console.log(a = 4); //4</p><p>\u8FD9\u5C31\u610F\u5473\u7740\uFF0C\u53EF\u4EE5\u8FDE\u7EED\u4F7F\u7528\u8D4B\u503C\u8FD0\u7B97\u7B26</p><p>var a, b, c; a = b = c = 15;</p><p>\u81EA\u589E\u548C\u81EA\u51CF<br> a++ \u5148\u7528\u518D\u52A0\uFF0C++a\u5148\u52A0\u5728\u7528</p><p>var a = 3; var b = a++; console.log(a); //4 console.log(b); //3</p><p>var a = 3; var b = ++a; console.log(a); //4 console.log(b); //4</p><h3 id="\u7EFC\u5408\u8868\u8FBE\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u7EFC\u5408\u8868\u8FBE\u5F0F" aria-hidden="true">#</a> \u7EFC\u5408\u8868\u8FBE\u5F0F</h3><p>\u7EFC\u5408\u8FD0\u7B97\u7684\u8FD0\u7B97\u987A\u5E8F<br> \u8FD0\u7B97\u987A\u5E8F\uFF1A \u975E\u8FD0\u7B97 -&gt; \u6570\u5B66\u8FD0\u7B97 -&gt; \u5173\u7CFB\u8FD0\u7B97 -&gt; \u903B\u8F91\u8FD0\u7B97</p><p>5 &lt; 3 + 3; //true 3 &gt; 2 &amp;&amp; 8 &gt; 4 +3; //true 3 &gt; 2 &amp;&amp; 8 &gt; 4 + 4; //false !13 &lt; 5-3; //true !13 &lt; 5 -5; //false</p>',119),n=[p];function t(d,u){return e(),l("div",null,n)}var o=a(r,[["render",t],["__file","chapter8.html.vue"]]);export{o as default};
